fun fetchNewsArticles(): List<NewsArticle> {
    return allNewsArticles
}
fun fetchBiasedNews(id:Int): List<BiasedArticles>{
    return allBiasedArticles.filter{it.articleId == id}
}
fun searchNewsArticles(query: String): List<NewsArticle> {
    if (query.isBlank()) return emptyList()
    val lowerCaseQuery = query.lowercase()

    return allNewsArticles.filter {
        it.headline.lowercase().contains(lowerCaseQuery) ||
                it.description.lowercase().contains(lowerCaseQuery) ||
                it.article.lowercase().contains(lowerCaseQuery)
    }.distinct()
}

fun searchCategories(category: String): List<NewsArticle> {
    return allNewsArticles.filter { it.category.equals(category, ignoreCase = true) }
}

fun getCachedSearchResults(): List<String> {
    return recentlySearched.toList()
}
fun addSearchToCache(query: String) {
    if (query.isBlank()) return
    recentlySearched.remove(query)
    recentlySearched.add(0, query)
}

fun removeSearchFromCache(query: String) {
    recentlySearched.remove(query)
}
fun saveArticle(Article: NewsArticle){
    savedArticles.add(Article)
}
fun removeArticle(Article: NewsArticle){
    savedArticles.remove(Article)
}
fun isArticleSaved(Article: NewsArticle): Boolean{
    return savedArticles.contains(Article)
}
fun getSavedArticles(): MutableList<NewsArticle>{
    return savedArticles
}
